import { configureStore ,createSlice } from "@reduxjs/toolkit";
const initialdata ={ counter: 0 , showCounter:true }
createSlice({
  name:'counter',
  initialState: initialdata,
  reducers:{
    increment(state){ state.counter++},
    decrement(state){state.counter--},
    increas(state,action){state.counter = state.counter + action.payload},
    toggleCounter(state){state.counter++},
  }
})

const counterReducer = (state = initialdata, action) => {
  if (action.type === "inc") {
    return {
      counter: state.counter + 1,
      showCounter:state.showCounter
    };
  }
  if (action.type === "incby5") {
    return {
      counter: state.counter + action.amount,
      showCounter:state.showCounter

    };
  }
  if (action.type === "dec") {
    return {
      counter: state.counter - 1,
      showCounter:state.showCounter

    };
  }
  if (action.type === "toggle") {
    return {
      counter: state.counter ,
      showCounter:!state.showCounter

    };
  }
  return state;
};

const store = configureStore({
  reducer: counterReducer,
});

export default store;
