import { configureStore ,createSlice } from "@reduxjs/toolkit";
const initialdata ={ counter: 0 , showCounter:true }
const Counter = 
createSlice({
  name:'counter',
  initialState: initialdata,
  reducers:{
    increment(state){ state.counter++},
    decrement(state){state.counter--},
    increas(state,action){state.counter = state.counter + action.payload},
    toggleCounter(state){state.showCounter = !state.showCounter},
  }
})



const store = configureStore({
  reducer:Counter,
  counter: Counter.reducer,
});
export const counterAction = Counter.actions;
export default store;
